import styled from "styled-components";
import { motion } from "framer-motion";
import {
    Button,
    Box,
    Text
} from "@primer/react"
import Keyboard from "./Keyboard";

const spacing = 4;

const Notification = (props) => {
    return (
        <motion.Box
            initial={{
                opacity: 0,
                y: 20
            }}
            animate={{
                opacity: 1,
                y: 0
            }}
            transition={{
                type: 'spring',
                duration: 0.75,
                delay: props.delay,
            }}
        >
            <NotificationButton onClick={props.onClick}>
                <Box display="flex" width="100%" flexDirection="column" py={2} borderBottom="1px solid">
                    <Box width="100%" display="flex" justifyContent="space-between" alignItems="center">
                        <Text color="fg.muted" fontSize="12px" as="p">{props.repo || 'github/repo #375'}</Text>
                        <Box display="flex">
                            <Text fontSize="12px" mr="2">{props.time || "1 hour ago"}</Text>
                            <Keyboard command="1" />
                        </Box>
                    </Box>
                    <Box display="flex" width="100%">
                        <Text as="p">{props.title || "Introduce new notifications"}</Text>
                    </Box>
                </Box>
            </NotificationButton>
        </motion.Box>
    )
}

export default Notification;

const NotificationButton = styled(Button)`
    display: block;
    width: 100%;
    border: none;
    background-color: transparent;
    padding: 0.25rem 1rem;
    border-radius: 6px;
    box-shadow: none;
    font-weight: normal;
`;
