import { Box, TextInput } from '@primer/react'
import styled from 'styled-components'
import {
    MarkGithubIcon,
    SearchIcon,
    BellIcon,
    IssueOpenedIcon,
    GitPullRequestIcon
} from '@primer/octicons-react'
import Command from './Command'

const current = "issues"

const commands = [
    {
        name: "Notifications dashboard",
        icon: BellIcon,
        command: "⌘N"
    },
    {
        name: "Issues",
        icon: IssueOpenedIcon,
        command: "⌘I"
    },
    {
        name: "Pull requests",
        icon: GitPullRequestIcon,
        command: "⌘P"
    },
];

const spacing = 4;

const Notifications = (props) => {
    return(
        <Box m={4} border="1px solid" borderRadius={12} borderColor="border.muted" maxWidth={"500px"}>
            {current === "home" &&
            <>
                <Box display="flex" p={spacing}  borderColor="border.muted">
                    <Box mr={3}>
                        <MarkGithubIcon size={24} />
                    </Box>
                    <h4>Notifications</h4>
                </Box>

                <Box p={spacing} display="flex" borderBottom="1px solid" borderTop="1px solid" borderColor="border.muted" alignItems="center" backgroundColor="canvas.inset" justifyItems="center" py={1}>
                    <SearchIcon />
                    <SearchBar placeholder="Search" backgroundColor="transparent" />
                </Box>

                <Box px={spacing - 2} py={3}>
                    {commands.map((command, index) => {
                        return(
                            <Command
                                name={command.name}
                                icon={command.icon}
                                command={command.command}
                            />
                        )
                    })}
                </Box>
            </>
            }
            {current === "issues" && (
                <h1>Issues</h1>
            )}
        </Box>
    )
}

export default Notifications;

const SearchBar = styled(TextInput)`
    background-color: transparent;
    border: none;
    outline: none;
    box-shadow: none;
    width: 100%;

    &:focus-within {
        border: none;
        outline: none;
        box-shadow: none;
    }
`;
