import styled from "styled-components";
import { motion } from "framer-motion";
import {
    Avatar,
    Button,
    Box,
    Text,
    AvatarPair
} from "@primer/react"
import Keyboard from "./Keyboard";

const spacing = 4;

const Notification = (props) => {
    return (
        <motion.div
            initial={{
                opacity: 0,
                y: 20
            }}
            animate={{
                opacity: 1,
                y: 0
            }}
            transition={{
                type: 'spring',
                duration: 0.75,
                delay: props.delay,
            }}
        >
            <Box display="flex" flexDirection="column" mx={3} borderBottom={props.last ? "" : "1px solid"} borderColor="border.muted">
                <NotificationButton onClick={props.onClick}>
                    <Box width="100%" display="flex" justifyContent="space-between" alignItems="center">
                        <Box display="flex">
                            <Box position="relative">
                                <Avatar src="https://avatars.githubusercontent.com/mona" />
                                {/* {props.new && <NewBubble></NewBubble>} */}
                                <Box bg="accent.emphasis" height="0.75rem" width="0.75rem" borderRadius="50%" position="absolute" top="-4px" left="-4px" border="2px solid" borderColor="canvas.default"></Box>
                            </Box>
                            <Text ml={2} color="fg.muted" fontSize="12px" as="p">{props.repo || 'github/repo #375'}</Text>
                        </Box>
                        <Box display="flex">
                            <Text color="fg.muted" fontSize="12px" mr="2">{props.time || "1 hour ago"}</Text>
                            <Keyboard command="1" />
                        </Box>
                    </Box>
                    <Box display="flex" width="100%">
                        <Text mt={2} fontWeight={props.new ? "bold" : "normal"} as="p">{props.title || "Introduce new notifications"}</Text>
                    </Box>
                </NotificationButton>
            </Box>
        </motion.div>
    )
}

export default Notification;

const NotificationButton = styled(Button)`
    display: block;
    width: 100%;
    border: none;
    background-color: transparent;
    padding: 12px;
    border-radius: 6px;
    box-shadow: none;
    font-weight: normal;
    margin: 8px 0;

    &:hover {
        background-color: transparent;
    }
`;
