import { useState } from 'react'
import { Box, TextInput } from '@primer/react'
import { Context } from './context/Context'
import styled from 'styled-components'
import {
    MarkGithubIcon,
    SearchIcon,
    BellIcon,
    IssueOpenedIcon,
    GitPullRequestIcon
} from '@primer/octicons-react'
import Command from './Command'

const current = "issues"

const commands = [
    {
        name: "Notifications dashboard",
        icon: BellIcon,
        command: "⌘N",
        value: "home"
    },
    {
        name: "Issues",
        icon: IssueOpenedIcon,
        command: "⌘I",
        value: "issues"
    },
    {
        name: "Pull requests",
        icon: GitPullRequestIcon,
        command: "⌘P",
        value: "prs"
    },
];

const Notifications = (props) => {
    const spacing = 4;
    const [ value, setValue ] = useState("home");

    return(
        <Box flex="0;" width="100%" boxShadow="shadow.large" bg="canvas.default" border="1px solid" borderRadius={12} borderColor="border.muted" maxWidth={"500px"}>
            <Box display="flex" borderColor="border.muted" px={4} py={3}>
                <Box mr={3}>
                    <MarkGithubIcon size={24} />
                </Box>
                <h4>Notifications</h4>
            </Box>

            <Box p={spacing} display="flex" borderBottom="1px solid" borderTop="1px solid" borderColor="border.muted" alignItems="center" backgroundColor="canvas.inset" justifyItems="center" py={1}>
                {value === "home" &&
                    <>
                        <SearchIcon />
                        <SearchBar placeholder="Search" backgroundColor="transparent" />
                    </>
                }
            </Box>
            <Box px={spacing - 2} py={3}>
                {current === "issues" &&
                    commands.map((command) => {
                        return(
                            <Command
                                name={command.name}
                                icon={command.icon}
                                command={command.command}
                                onClick={() => setValue(command.value)}
                            />
                        )
                    })
                }
            </Box>
        </Box>
    )
}

export default Notifications;

const SearchBar = styled(TextInput)`
    background-color: transparent;
    border: none;
    outline: none;
    box-shadow: none;
    width: 100%;

    &:focus-within {
        border: none;
        outline: none;
        box-shadow: none;
    }
`;
