import {
    Box,
    Text
} from "@primer/react"
import Keyboard from "./Keyboard";

const spacing = 4;

const Notification = (props) => {
    return (
        <Box display="flex" width="100%" flexDirection="column" p={spacing}>
            <Box width="100%" display="flex" justifyContent="space-between" alignItems="center">
                <Text color="fg.muted" fontSize="12px" as="p">{props.repo || 'github/repo #375'}</Text>
                <Box>
                    <Text fontSize="12px" mr="2">{props.time || "1 hour ago"}</Text>
                    <Keyboard command="1" />
                </Box>
            </Box>
            <Box display="flex" width="100%">
                <Text as="p">{props.title || "Introduce new notifications"}</Text>
            </Box>
        </Box>
    )
}

export default Notification;
