import {
    Avatar,
    Box,
    Text
} from '@primer/react';

const IssueHeader = (props) => {
    return(
        <Box width="100%" display="flex" justifyContent="space-between" alignItems="center">
            <Box display="flex">
                <Box position="relative">
                    <Avatar src="https://avatars.githubusercontent.com/mona" />
                    {props.new && <Box bg="accent.emphasis" height="0.75rem" width="0.75rem" borderRadius="50%" position="absolute" top="-4px" left="-4px" border="2px solid" borderColor="canvas.default"></Box>}
                </Box>
                <Text ml={2} color="fg.muted" fontSize="12px" as="p">{props.repo || 'github/repo #375'}</Text>
            </Box>
            <Box display="flex">
                <Text color="fg.muted" fontSize="12px" mr="2">{props.time || "1 hour ago"}</Text>
                <Keyboard command="1" />
            </Box>
        </Box>
    )
}

export default IssueHeader;
