import { Box, Text, Link, StyledOcticon, TextInput } from '@primer/react'
import styled from 'styled-components'
import {
    MarkGithubIcon,
    CheckIcon,
    CommentIcon,
    MortarBoardIcon,
    SearchIcon,
} from '@primer/octicons-react'

const Notifications = (props) => {
    return(
        <Box m={8} border="1px solid" borderRadius={2} borderColor="border.muted" maxWidth={"500px"}>
            <Box display="flex" p={3}  borderColor="border.muted">
                <Box mr={2}>
                    <MarkGithubIcon size={24} />
                </Box>
                <h4>Notifications</h4>
            </Box>

            <Box display="flex" borderBottom="1px solid" borderTop="1px solid" borderColor="border.muted" alignItems="center" backgroundColor="canvas.inset" justifyItems="center" px={4} py={2}>
                <SearchIcon />
                <SearchBar placeholder="Search" backgroundColor="transparent" />
            </Box>


        </Box>
    )
}

export default Notifications;

const SearchBar = styled(TextInput)`
    background-color: transparent;
    border: none;
    outline: none;
    box-shadow: none;
    width: 100%;
    background: pink;

    &:focus-within {
        border: none;
        outline: none;
        box-shadow: none;
    }
`;
