import { useState } from 'react'
import { Box, TextInput, Breadcrumbs, StyledOcticon } from '@primer/react'
import styled from 'styled-components'
import {
    MarkGithubIcon,
    SearchIcon,
    BellIcon,
    IssueOpenedIcon,
    GitPullRequestIcon
} from '@primer/octicons-react'
import Command from './Command'

const commands = [
    {
        name: "Notifications dashboard",
        icon: BellIcon,
        command: "âŒ˜N",
        value: "home"
    },
    {
        name: "Issues",
        icon: IssueOpenedIcon,
        command: "âŒ˜I",
        value: "issues"
    },
    {
        name: "Pull requests",
        icon: GitPullRequestIcon,
        command: "âŒ˜P",
        value: "prs"
    },
];

const issues = [
    {
        name: "Assigned",
        emoji: "ðŸŽ¯",
        command: "âŒ˜A",
        value: "issues"
    },
    {
        name: "Participating",
        emoji: "ðŸ’¬",
        command: "âŒ˜P",
        value: "issues"
    },
    {
        name: "Mentioned",
        emoji: "âœ‹",
        command: "âŒ˜M",
        value: "issues"
    },
    {
        name: "Team mentioned",
        emoji: "ðŸ™Œ",
        command: "âŒ˜T",
        value: "issues"
    },
    {
        name: "Review requested",
        emoji: "ðŸ‘€",
        command: "âŒ˜R",
        value: "issues"
    },
];

const Notifications = (props) => {
    const spacing = 4;
    const [ value, setValue ] = useState("home");

    return(
        <Box flex="0;" width="100%" boxShadow="shadow.large" bg="canvas.default" border="1px solid" borderRadius={12} borderColor="border.muted" maxWidth={"500px"}>
            <Box display="flex" borderColor="border.muted" px={4} py={3}>
                <Box mr={3}>
                    <MarkGithubIcon size={24} />
                </Box>
                <h4>Notifications</h4>
            </Box>

            <Box p={spacing} display="flex" borderBottom="1px solid" borderTop="1px solid" borderColor="border.muted" alignItems="center" backgroundColor="canvas.inset" justifyItems="center" py={1}>
                {value === "home" ?
                    <>
                        <SearchIcon />
                        <SearchBar placeholder="Search" backgroundColor="transparent" />
                    </>
                    :
                    <Breadcrumbs>
                        <StyledOcticon icon={BellIcon} />
                        <Breadcrumbs.Item href="/">Issues</Breadcrumbs.Item>
                        <Breadcrumbs.Item href="/about/team" selected>
                            Home
                        </Breadcrumbs.Item>
                    </Breadcrumbs>
                }
            </Box>
            <Box px={spacing - 2} py={3}>
                {value === "home" &&
                    commands.map((command) => {
                        return(
                            <Command
                                name={command.name}
                                icon={command.icon}
                                command={command.command}
                                onClick={() => setValue(command.value)}
                            />
                        )
                    })
                }
                {value === "issues" &&
                    issues.map((command) => {
                        return(
                            <Command
                                name={command.name}
                                emoji={command.emoji}
                                command={command.command}
                                onClick={() => setValue(command.value)}
                            />
                        )
                    })
                }
            </Box>
        </Box>
    )
}

export default Notifications;

const SearchBar = styled(TextInput)`
    background-color: transparent;
    border: none;
    outline: none;
    box-shadow: none;
    width: 100%;

    &:focus-within {
        border: none;
        outline: none;
        box-shadow: none;
    }
`;
