import styled from 'styled-components'
import { motion } from 'framer-motion'
import { Box, Text, StyledOcticon, Button } from '@primer/react'

const Command = (props) => {
    return(
        <CommandButton
            initial={{
                opacity: 0,
                y: "16px"
            }}
            animate={{ opacity: 1, y: "0" }}
            transition={{
                duration: 0.2,
                delay: props.delay,
            }}
            exit={{ opacity: 0 }}
            onClick={props.onClick}
        >
            <Box py={2} display="flex" alignItems="center" alignContent="center" justifyContent="space-between">
                <Box display="inline-flex" alignItems="center">
                    {props.icon ? <StyledOcticon icon={props.icon || 'IssueOpenedIcon'} color="neutral.emphasis" /> : <Box width="16px" justifyContent="center" display="block">{props.emoji}</Box>}
                    <Text color="fg.default" ml={'12px'}>{props.name || "Command name"}</Text>
                </Box>
                <Text color="fg.default">{props.command || "⌘"}</Text>
            </Box>
        </CommandButton>

    )
}

export default Command;

const CommandButton = styled(motion.Button)`
    display: block;
    width: 100%;
    border: none;
    background-color: transparent;
    cursor: pointer;
    padding: 2px 16px;
    border-radius: 6px;
    box-shadow: none;
    font-weight: normal;

    &:hover {
        background: #F5F8FA;

        svg {
            color: #232930;
        }
    }
`;
