import { createContext, useState } from 'react';

const PageContext = createContext();

export const Context = ({ children }) => {
    const [value, setValue] = useState("home");

    return (
        <AuthContext.Provider value={{ value, setValue }}>{children}</AuthContext.Provider>
    );
};

export const CollapsedContext = createContext();

const CollapsedProvider = (props) => {
    const [collapsed, toggleCollapse] = useState(false);
    const toggleFunction = () => {
        toggleCollapse(!collapsed);
    }

    return(
        <CollapsedContext.Provider value={{ collapsed, toggleFunction }}>
            {props.children}
        </CollapsedContext.Provider>
    );
}

export default CollapsedProvider;
