import {
    useContext,
    useEffect,
    useState
} from 'react'
import {
    Box,
    Breadcrumbs,
    StyledOcticon,
    Text,
    TextInput,
} from '@primer/react'
import styled from 'styled-components'
import {
    MarkGithubIcon,
    SearchIcon,
    BellIcon,
    IssueOpenedIcon,
    GitPullRequestIcon
} from '@primer/octicons-react'
import Command from './Command'
import Notification from './Notification'

const commands = [
    {
        name: "Notifications dashboard",
        icon: BellIcon,
        command: "âŒ˜N",
        value: "home"
    },
    {
        name: "Issues",
        icon: IssueOpenedIcon,
        command: "âŒ˜I",
        value: "issues"
    },
    {
        name: "Pull requests",
        icon: GitPullRequestIcon,
        command: "âŒ˜P",
        value: "prs"
    },
];

const issues = [
    {
        name: "Assigned",
        emoji: "ðŸŽ¯",
        command: "âŒ˜A",
        value: "issues"
    },
    {
        name: "Participating",
        emoji: "ðŸ’¬",
        command: "âŒ˜P",
        value: "issues"
    },
    {
        name: "Mentioned",
        emoji: "âœ‹",
        command: "âŒ˜M",
        value: "issues"
    },
    {
        name: "Team mentioned",
        emoji: "ðŸ™Œ",
        command: "âŒ˜T",
        value: "issues"
    },
    {
        name: "Review requested",
        emoji: "ðŸ‘€",
        command: "âŒ˜R",
        value: "issues"
    },
];

const mentions = [
    {
        repo: "primer/primer",
        title: "Add a new section to the Primer",
        time: "1 hour ago",
    },
    {
        repo: "primer/primer",
        title: "Add a new section to the Primer",
        time: "1 hour ago",
    },
    {
        repo: "primer/primer",
        title: "Add a new section to the Primer",
        time: "1 hour ago",
    },
]

const Notifications = (props) => {
    const [ page, setPage ] = useState("home");
    const [ key, setKey ] = useState("");

    const handleKeyDown = (event) => {
        setKey(event.keyCode)

        if(event.keyCode === 73) {
            setPage("issues");

        };

        if(event.keyCode === 77) {
            setPage("mentioned")
        }

        if(event.keyCode === 27) {
            setPage("home")
        };
    };

    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);

        return () => {
            window.removeEventListener('keydown', handleKeyDown);
        };
    }, []);
    const spacing = 4;

    return(
        <Box height="max-content" width="100%" boxShadow="shadow.large" bg="canvas.default" border="1px solid" borderRadius={12} borderColor="border.muted" maxWidth={"500px"}>
            <Box position="absolute" bottom="1rem" left="1rem" display="flex">
                <Box mr="2rem">
                    <Text color="fg.muted">Key</Text>
                    <Text as="h1">{key}</Text>
                </Box>

                <Box>
                    <Text color="fg.muted">Location</Text>
                    <Text as="h1">{page}</Text>
                </Box>
            </Box>
            <Box display="flex" borderColor="border.muted" px={4} py={3}>
                <Box mr={3}>
                    <MarkGithubIcon size={24} />
                </Box>
                <h4>Notifications</h4>
            </Box>

            <Box height="44px" p={spacing} display="flex" borderBottom="1px solid" borderTop="1px solid" borderColor="border.muted" alignItems="center" backgroundColor="canvas.inset" justifyItems="center" py={1}>
                {page === "home" ?
                    <>
                        <SearchIcon />
                        <SearchBar placeholder="Search" backgroundColor="transparent" />
                    </>
                    :
                    <Breadcrumbs>
                        <Breadcrumbs.Item href="/">
                            <StyledOcticon icon={IssueOpenedIcon} />
                            <Text ml={'12px'}>
                                Issues
                            </Text>
                        </Breadcrumbs.Item>
                        <Breadcrumbs.Item href="/about/team" selected>
                            {page === "issues" ? "" : "Mentioned"}
                        </Breadcrumbs.Item>
                    </Breadcrumbs>
                }
            </Box>
            <Box px={spacing - 2} py={2}>
                {page === "home" &&
                    commands.map((command, index) => {
                        return(
                                <Command
                                    name={command.name}
                                    icon={command.icon}
                                    command={command.command}
                                    onClick={() => setPage(command.value)}
                                    delay={index * 0.1}
                                />
                        )
                    })
                }
                {page === "issues" &&
                    issues.map((command, index) => {
                        return(
                            <Command
                                name={command.name}
                                emoji={command.emoji}
                                command={command.command}
                                onClick={() => setPage(command.value)}
                                delay={index * 0.1}
                            />
                        )
                    })
                }
                {page === "mentioned" &&
                    mentions.map((mention, index) => {
                        return(
                            <Notification
                                repo={mention.repo}
                                title={mention.title}
                                delay={index * 0.1}
                                last={index === mentions.length - 1}
                            />
                        )
                    })
                }
            </Box>
        </Box>
    )
}

export default Notifications;

const SearchBar = styled(TextInput)`
    background-color: transparent;
    border: none;
    outline: none;
    box-shadow: none;
    width: 100%;

    &:focus-within {
        border: none;
        outline: none;
        box-shadow: none;
    }
`;
