import { useOnOutsideClick } from './useOnOutsideClick';
import { useOpenAndCloseFocus } from './useOpenAndCloseFocus';
import { useOnEscapePress } from './useOnEscapePress';
import { useProvidedRefOrCreate } from './useProvidedRefOrCreate';
export const useOverlay = ({
  overlayRef: _overlayRef,
  returnFocusRef,
  initialFocusRef,
  onEscape,
  ignoreClickRefs,
  onClickOutside,
  preventFocusOnOpen
}) => {
  const overlayRef = useProvidedRefOrCreate(_overlayRef);
  useOpenAndCloseFocus({
    containerRef: overlayRef,
    returnFocusRef,
    initialFocusRef,
    preventFocusOnOpen
  });
  useOnOutsideClick({
    containerRef: overlayRef,
    ignoreClickRefs,
    onClickOutside
  }); // We only want one overlay to close at a time

  const preventeddefaultCheckedEscape = event => {
    onEscape(event);
    event.preventDefault();
  };

  useOnEscapePress(preventeddefaultCheckedEscape);
  return {
    ref: overlayRef
  };
};